_source.option_example:
{
  alertmanager = {
    interval = "15s";
    servers = [
      {
        name = "prod";
        uri = "http://alertmanager.example.com";
      }
    ];
  };
  listen = {
    address = "192.168.1.4";
    port = "8000";
    prefix = "/dashboard";
  };
}

{
  dns-server-timeout = 100;
  forward-ddns = {
    ddns-domains = [ ];
  };
  ip-address = "127.0.0.1";
  ncr-format = "JSON";
  ncr-protocol = "UDP";
  port = 53001;
  reverse-ddns = {
    ddns-domains = [ ];
  };
  tsig-keys = [ ];
}

{
  interfaces-config = {
    interfaces = [
      "eth0"
    ];
  };
  lease-database = {
    name = "/var/lib/kea/dhcp4.leases";
    persist = true;
    type = "memfile";
  };
  rebind-timer = 2000;
  renew-timer = 1000;
  subnet4 = [
    {
      pools = [
        {
          pool = "192.0.2.100 - 192.0.2.240";
        }
      ];
      subnet = "192.0.2.0/24";
    }
  ];
  valid-lifetime = 4000;
}

{
  interfaces-config = {
    interfaces = [
      "eth0"
    ];
  };
  lease-database = {
    name = "/var/lib/kea/dhcp6.leases";
    persist = true;
    type = "memfile";
  };
  preferred-lifetime = 3000;
  rebind-timer = 2000;
  renew-timer = 1000;
  subnet6 = [
    {
      pools = [
        {
          pool = "2001:db8:1::1-2001:db8:1::ffff";
        }
      ];
      subnet = "2001:db8:1::/64";
    }
  ];
  valid-lifetime = 4000;
}

{
  default = {
    ids = [ "*" ];
    settings = {
      main = {
        capslock = "overload(control, esc)";
      };
    };
  };
  externalKeyboard = {
    ids = [ "1ea7:0907" ];
    settings = {
      main = {
        esc = capslock;
      };
    };
  };
}

{
  main = {
    capslock = "overload(control, esc)";
    rightalt = "layer(rightalt)";
  };
  rightalt = {
    h = "left";
    j = "down";
    k = "up";
    l = "right";
  };
}

