{
  serverAliases = [
    "librenms.${config.networking.domain}"
  ];
  # To enable encryption and let let's encrypt take care of certificate
  forceSSL = true;
  enableACME = true;
  # To set the LibreNMS virtualHost as the default virtualHost;
  default = true;
}

[
  {
    addr = "195.154.1.1";
    port = 443;
    ssl = true;
  }
  {
    addr = "192.154.1.1";
    port = 80;
  }
  {
    addr = "unix:/var/run/nginx.sock";
  }
]

{ private-or-clear = ''
    # Attempt opportunistic IPsec for the entire Internet
    0.0.0.0/0
    ::/0
  '';
}

{
  hostName = "survey.example.org";
  adminAddr = "webmaster@example.org";
  forceSSL = true;
  enableACME = true;
}

{
  # global options
  header = {
    dateext = true;
  };
  # example custom files
  "/var/log/mylog.log" = {
    frequency = "daily";
    rotate = 3;
  };
  "multiple paths" = {
     files = [
      "/var/log/first*.log"
      "/var/log/second.log"
    ];
  };
  # specify custom order of sections
  "/var/log/myservice/*.log" = {
    # ensure lower priority
    priority = 110;
    postrotate = ''
      systemctl reload myservice
    '';
  };
};

''
  if [type] == "syslog" {
    # Keep only relevant systemd fields
    # https://www.freedesktop.org/software/systemd/man/systemd.journal-fields.html
    prune {
      whitelist_names => [
        "type", "@timestamp", "@version",
        "MESSAGE", "PRIORITY", "SYSLOG_FACILITY"
      ]
    }
  }
''

''
  # Read from journal
  pipe {
    command => "${config.systemd.package}/bin/journalctl -f -o json"
    type => "syslog" codec => json {}
  }
''

''
  redis { host => ["localhost"] data_type => "list" key => "logstash" codec => json }
  elasticsearch { }
''
