_source.option_example:
root = {
  user = ".+";
  collection = "";
  permissions = "R";
};
principal = {
  user = ".+";
  collection = "{user}";
  permissions = "RW";
};
calendars = {
  user = ".+";
  collection = "{user}/[^/]+";
  permissions = "rw";
};

server = {
  hosts = [ "0.0.0.0:5232" "[::]:5232" ];
};
auth = {
  type = "htpasswd";
  htpasswd_filename = "/etc/radicale/users";
  htpasswd_encryption = "bcrypt";
};
storage = {
  filesystem_folder = "/var/lib/radicale/collections";
};

[
  {
    doNotRedirect = [
      "-d 1.2.0.0/16"
    ];
    port = 23456;
    proxy = "1.2.3.4:8080";
    redirectCondition = "--dport 80";
    type = "http-relay";
  }
  {
    doNotRedirect = [
      "-d 1.2.0.0/16"
    ];
    port = 23457;
    proxy = "1.2.3.4:8080";
    redirectCondition = true;
    type = "http-connect";
  }
]

[
  {
    directory = "/home/user/sync_test";
    knownHosts = [
      "192.168.1.2:4444"
      "192.168.1.3:4444"
    ];
    searchLAN = true;
    secretFile = "/run/resilio-secret";
    useDHT = false;
    useRelayServer = true;
    useSyncTrash = true;
    useTracker = true;
  }
]

{
  localbackup = {
    exclude = [
      "/home/*/.cache"
    ];
    initialize = true;
    passwordFile = "/etc/nixos/secrets/restic-password";
    paths = [
      "/home"
    ];
    repository = "/mnt/backup-hdd";
  };
  remotebackup = {
    extraOptions = [
      "sftp.command='ssh backup@host -i /etc/nixos/secrets/backup-private-key -s sftp'"
    ];
    passwordFile = "/etc/nixos/secrets/restic-password";
    paths = [
      "/home"
    ];
    repository = "sftp:backup@host:/backups/home";
    timerConfig = {
      OnCalendar = "00:05";
      RandomizedDelaySec = "5h";
    };
  };
}

_source.option_default:
{
  extraOpts = ''
    open_files=2000
    filter_bits=12
    cache_mb=256
    file_size_pb=8
    file_size_mult=2;
  '';
  type = "rocksdb";
}

{
  peer = {
    ip = "0.0.0.0";
    port = 51235;
    protocol = [
      "peer"
    ];
  };
  rpc = {
    admin = [
      "127.0.0.1"
    ];
    port = 5005;
    protocol = [
      "http"
    ];
  };
  ws_public = {
    ip = "0.0.0.0";
    port = 5006;
    protocol = [
      "ws"
      "wss"
    ];
  };
}

_source.option_example:
{
  resolvers.cloudflare-dot = {
    address = "1.1.1.1:853";
    protocol = "dot";
  };
  groups.cloudflare-cached = {
    type = "cache";
    resolvers = ["cloudflare-dot"];
  };
  listeners.local-udp = {
    address = "127.0.0.1:53";
    protocol = "udp";
    resolver = "cloudflare-cached";
  };
  listeners.local-tcp = {
    address = "127.0.0.1:53";
    protocol = "tcp";
    resolver = "cloudflare-cached";
  };
}

{
  normal = {
    includes = [ "$CONFDIR/worker-normal.inc" ];
    bindSockets = [{
      socket = "/run/rspamd/rspamd.sock";
      mode = "0660";
      owner = "${config.services.rspamd.user}";
      group = "${config.services.rspamd.group}";
    }];
  };
  controller = {
    includes = [ "$CONFDIR/worker-controller.inc" ];
    bindSockets = [ "[::1]:11334" ];
  };
}

{
  system.enabled_bridges = [ "*" ];
  error = {
    output = "http";
    report_limit = 5;
  };
  FileCache = {
    enable_purge = true;
  };
}

{
  cvs = {
    "auth users" = [
      "tridge"
      "susan"
    ];
    comment = "CVS repository (requires authentication)";
    path = "/data/cvs";
    "secrets file" = "/etc/rsyncd.secrets";
  };
  ftp = {
    comment = "whole ftp area";
    path = "/var/ftp/./pub";
  };
  global = {
    gid = "nobody";
    "max connections" = 4;
    uid = "nobody";
    "use chroot" = true;
  };
}

{
  type = "hybrid-s3"
  s3_access_key_file = konfig.age.secrets.R2_ACCESS_KEY.path;
  s3_secret_key_file = konfig.age.secrets.R2_SECRET_KEY.path;
  s3_bucket = "my_bucket";
  s3_url = "https://s3.example.com";
}
