_source.option_example:
settings = {
  repositories = [
    { name = "tabby"; git_url = "https://github.com/TabbyML/tabby.git"; }
    { name = "CTranslate2"; git_url = "git@github.com:OpenNMT/CTranslate2.git"; }

    # local directory is also supported, but limited by systemd DynamicUser=1
    # adding local repositories will need to be done manually
    { name = "repository_a"; git_url = "file:///var/lib/tabby/repository_a"; }
  ];
};

{
  address = "192.0.2.0";
  router = {
    address = "192.0.2.1";
  };
  pool = {
    address = "192.0.2.1";
    prefixLength = 24;
  };
}

{
  address = "2001:db8::1";
  router = {
    address = "64:ff9b::1";
  };
  pool = {
    address = "64:ff9b::";
    prefixLength = 96;
  };
}

{
  inputs = {
    statsd = {
      delete_timings = true;
      service_address = ":8125";
    };
  };
  outputs = {
    influxdb = {
      database = "telegraf";
      urls = [
        "http://localhost:8086"
      ];
    };
  };
}

{
  teleport = {
    nodename = "client";
    advertise_ip = "192.168.1.2";
    auth_token = "60bdc117-8ff4-478d-95e4-9914597847eb";
    auth_servers = [ "192.168.1.1:3025" ];
    log.severity = "DEBUG";
  };
  ssh_service = {
    enabled = true;
    labels = {
      role = "client";
    };
  };
  proxy_service.enabled = false;
  auth_service.enabled = false;
}

{
  reverseProxy = true;
  defaults = {
    name = "Your Network";
    host = "localhost";
    port = 6697;
  };
}

{
  host1 = {
    addresses = [
      { address = "192.168.1.42"; }
      { address = "192.168.1.42"; port = 1655; }
    ];
    subnets = [ { address = "10.0.0.42"; } ];
    rsaPublicKey = "...";
    settings = {
      Ed25519PublicKey = "...";
    };
  };
  host2 = {
    subnets = [ { address = "10.0.1.0"; prefixLength = 24; weight = 2; } ];
    rsaPublicKey = "...";
    settings = {
      Compression = 10;
    };
  };
}

{
          Port 8888;
          Listen 127.0.0.1;
          Timeout 600;
          Allow 127.0.0.1;
          Anonymous = ['"Host"' '"Authorization"'];
          ReversePath = '"/example/" "http://www.example.com/"';
        }

{
  http = {
    routers = {
      router1 = {
        rule = "Host(`localhost`)";
        service = "service1";
      };
    };
    services = {
      service1 = {
        loadBalancer = {
          servers = [
            {
              url = "http://localhost:8080";
            }
          ];
        };
      };
    };
  };
}

{
  api = { };
  entryPoints = {
    http = {
      address = ":80";
    };
    web = {
      address = ":8080";
    };
  };
}

_source.option_default:
{
  refresh_rate = "30 seconds";
  appenders.stdout.kind = "console";
  root = {
    level = "warn";
    appenders = [ "stdout" ];
  };
  loggers = {
    tremor_runtime = {
      level = "debug";
      appenders = [ "stdout" ];
      additive = false;
    };
    tremor = {
      level = "debug";
      appenders = [ "stdout" ];
      additive = false;
    };
  };
}

