_source.option_example:
{
  hub_description = "Yet another ADC hub";
  hub_name = "My Public Hub";
  max_users = 150;
  server_bind_addr = "any";
  server_port = 1511;
}

{
  emu1 = {
    file = "/var/log/ulogd_pkts.log";
    sync = 1;
  };
  global = {
    stack = [
      "log1:NFLOG,base1:BASE,ifi1:IFINDEX,ip2str1:IP2STR,print1:PRINTPKT,emu1:LOGEMU"
      "log1:NFLOG,base1:BASE,pcap1:PCAP"
    ];
  };
  log1 = {
    group = 2;
  };
  pcap1 = {
    file = "/var/log/ulogd.pcap";
    sync = 1;
  };
}

{
  server = {
    interface = [ "127.0.0.1" ];
  };
  forward-zone = [
    {
      name = ".";
      forward-addr = "1.1.1.1@853#cloudflare-dns.com";
    }
    {
      name = "example.org.";
      forward-addr = [
        "1.1.1.1@853#cloudflare-dns.com"
        "1.0.0.1@853#cloudflare-dns.com"
      ];
    }
  ];
  remote-control.control-enable = true;
};

{
  type = "emperor";
  vassals = {
    moin = {
      type = "normal";
      pythonPackages = self: with self; [ moinmoin ];
      socket = "${config.services.uwsgi.runDir}/uwsgi.sock";
    };
  };
}
