_source.option_example:
{
  autosave = true;
  cpu = true;
  opencl = false;
  cuda = false;
  pools = [
    {
      url = "pool.supportxmr.com:443";
      user = "your-wallet";
      keepalive = true;
      tls = true;
    }
  ]
}

{
  inbounds = [
    {
      listen = "127.0.0.1";
      port = 1080;
      protocol = "http";
    }
  ];
  outbounds = [
    {
      protocol = "freedom";
    }
  ];
}

